css:
.root {
  font: menu;
}

.hint {
  font-size: 12px;
  padding: 2px;
  color: black;
  background-color: #bb9af7;
  border: solid 1px rgba(0, 0, 0, 0.5);
  text-transform: uppercase;
  font-weight: bold;
  line-height: 1;
  white-space: nowrap;
}

.highlighted {
  background-color: #9ece6a;
}

.matchedChars {
  opacity: 0.3;
}

.matchedText {
  border-bottom: 2px solid #ef0fff;
  box-sizing: border-box;
}

.status {
  font-size: 14px;
  padding: 4px 6px;
  color: white;
  background-color: black;
  box-shadow: 0 0 1px 0 rgba(255, 255, 255, 0.5);
  bottom: 0;
  right: 0;
  line-height: 1;
}

.peek .hint:not(.hasMatchedChars):not(.highlighted) {
  opacity: 0.2;
}

.mixedCase {
  text-transform: none;
}

.hidden,
.hint:not(.hidden) ~ .shruggie,
.status:empty {
  opacity: 0 !important;
}
